    private void BtnListoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnListoActionPerformed

        MetodosEmpleados metodos = new MetodosEmpleados();
        String id_pedidos = InputFilaNo.getText();

        if (!id_pedidos.isEmpty()) {

            int resultado = metodos.act(id_pedidos);

            if (resultado > 0) {
                JOptionPane.showMessageDialog(null, "El estado del pedido " + id_pedidos + " ha sido actualizado.");

                Controlador c = new Controlador();

                UtilidadesTablas.tablaNormal(tablePedido);
                c.datosPedidos(tablePedido);
                // Eliminar fila de la tabla en la interfaz

                InputFilaNo.setText("");
            } else {
                JOptionPane.showMessageDialog(null, "Error al actualizar el estado.");
            }
        } else {
            JOptionPane.showMessageDialog(null, "Por favor, ingrese el id del pedido.");
        }


    }//GEN-LAST:event_BtnListoActionPerformed

