    private void btnEsperaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEsperaActionPerformed
        String mesa = (String) boxMesaPedido.getSelectedItem();
            String mesero = (String) boxMesero.getSelectedItem();
            String categoria = (String) boxCategor.getSelectedItem();
            String producto = (String) boxPedido.getSelectedItem();
            String cantidad = InputCantidad.getText();

            // Verificar si la cantidad es un número
            try {
                
                // Intenta convertir la cantidad a un número entero
                int cantidadNumerica = Integer.parseInt(cantidad);

                // Si la conversión es exitosa, crea el pedido
                Pedido pedido = new Pedido();
                pedido.setMesa(mesa);
                pedido.setMesero(mesero);
                pedido.setCategoria(categoria);
                pedido.setProducto(producto);
                pedido.setCantidad(cantidad);

                // Agrega el pedido y realiza otras operaciones
                CerrarCaja.agregarPedido(pedido);
                Controlador c = new Controlador();

                if(mesa.equals("Seleccione Mesa...") || mesero.equals("Seleccione mesero...") || categoria.equals("Seleccione categoría...") || producto.equals("Seleccione pedido...") || cantidad.isEmpty()) {
                    JOptionPane.showMessageDialog(null, "Por favor, ingrese los campos faltantes.");
                } else {
                    c.ingresarPedido(mesa, mesero, producto, cantidad, categoria, tablePedido);
                    boxMesero.setSelectedIndex(0);
                    boxPedido.setSelectedIndex(0);
                    InputCantidad.setText("");
                    boxMesaPedido.setSelectedIndex(0);
                }
            } catch (NumberFormatException e) {
                // Si la conversión falla, muestra un mensaje de error
                JOptionPane.showMessageDialog(null, "La cantidad debe ser un número válido.");
            }
    }//GEN-LAST:event_btnEsperaActionPerformed

    private void btnSeleccionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSeleccionarActionPerformed
        
        String[] Bebidas = {"Sake", "Amazake", "Ramune", "Agua"};
        String[] Platos = {"Sushi", "Onigiri", "Tonkatsu", "Tempura", "Yakitori", "Okonomiyaki", "Oyakodon", "Katsudon", "Nikujaga", "Karaage"};
        boxPedido.removeAllItems();
        String categoriaSeleccionada = (String) boxCategor.getSelectedItem();
        if (categoriaSeleccionada.equals("Bebidas")) {
            for (String bebida : Bebidas) {
                boxPedido.addItem(bebida);
            }

        } else if (categoriaSeleccionada.equals("Platos")) {
            boxPedido.removeAllItems();
            for (String plato : Platos) {
                boxPedido.addItem(plato);
            }
        }
    }//GEN-LAST:event_btnSeleccionarActionPerformed

    private void boxMeseroFocusGained(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_boxMeseroFocusGained
        // Limpiar el JComboBox

        boxMesero.removeAllItems();
        // Agregar "Seleccione mesero..." al inicio de la lista
        boxMesero.addItem("Seleccione mesero...");
        boxMesero.setSelectedIndex(0);

        MetodosEmpleados me = new MetodosEmpleados();
        // Obtener los nombres de los meseros desde la base de datos
        List<String> nombresMeseros = me.obtenerNombresMeseros();

        // Agregar los nombres de los meseros al JComboBox
        for (String nombre : nombresMeseros) {
            boxMesero.addItem(nombre);
        }
    }//GEN-LAST:event_boxMeseroFocusGained

